pass.vertexshader:
#include "pcRedVertexShaderMacros.h" #include "pcRedVertexShaderConstants.h"
dcl_position V_POS dcl_texcoord V_TEXCOORDS
// decompress position mul R_POS, c[C_POS_DECOMPRESS0], V_POS add R_POS, c[C_POS_DECOMPRESS1], R_POS
; scale positions to correct size mul R_POS, R_POS, c[C_CUSTOM_MIN] ; project z mov R_TEMP.xyw, R_POS.xyz dp4 R_TEMP.z, R_POS, c[C_CUSTOM_MIN+1] mov oPos, R_TEMP
; transform uv coordinates to viewport (scale and offset) mad oT0.xy, V_TEXCOORDS.xy, c[C_CUSTOM_MIN+2].xy, c[C_CUSTOM_MIN+2].zw mov oT0.zw, c[C_CONST0].xz
